mapper.select:

select 

from cust_info
where cust_NO = #{custNo,jdbcType=CHAR}
  and sh_seat_NO = #{shSeatNo,jdbcType=CHAR}
  and "settle _no" = #{settleNo,jdbcType=CHAR}
  and szt_NO = #{sztNo,jdbcType=CHAR}
  and true_file_name = #{trueFileName,jdbcType=VARCHAR}

mapper.delete:

delete from cust_info
where cust_NO = #{custNo,jdbcType=CHAR}
  and sh_seat_NO = #{shSeatNo,jdbcType=CHAR}
  and "settle _no" = #{settleNo,jdbcType=CHAR}
  and szt_NO = #{sztNo,jdbcType=CHAR}
  and true_file_name = #{trueFileName,jdbcType=VARCHAR}

mapper.update:

update cust_info


    custname = #{custname,jdbcType=VARCHAR},


    sh_account_NO = #{shAccountNo,jdbcType=CHAR},


    sz_account_NO = #{szAccountNo,jdbcType=CHAR},


    send_type = #{sendType,jdbcType=CHAR},


    send_type_description = #{sendTypeDescription,jdbcType=VARCHAR},


    mail_address = #{mailAddress,jdbcType=VARCHAR},


where cust_NO = #{custNo,jdbcType=CHAR}
  and sh_seat_NO = #{shSeatNo,jdbcType=CHAR}
  and "settle _no" = #{settleNo,jdbcType=CHAR}
  and szt_NO = #{sztNo,jdbcType=CHAR}
  and true_file_name = #{trueFileName,jdbcType=VARCHAR}


update cust_info
set custname = #{custname,jdbcType=VARCHAR},
  sh_account_NO = #{shAccountNo,jdbcType=CHAR},
  sz_account_NO = #{szAccountNo,jdbcType=CHAR},
  send_type = #{sendType,jdbcType=CHAR},
  send_type_description = #{sendTypeDescription,jdbcType=VARCHAR},
  mail_address = #{mailAddress,jdbcType=VARCHAR}
where cust_NO = #{custNo,jdbcType=CHAR}
  and sh_seat_NO = #{shSeatNo,jdbcType=CHAR}
  and "settle _no" = #{settleNo,jdbcType=CHAR}
  and szt_NO = #{sztNo,jdbcType=CHAR}
  and true_file_name = #{trueFileName,jdbcType=VARCHAR}
